package 확인문제;

public class _05_ForFunctionCalculate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int x;
 int y;
 for (x=1; x<=10; x++) {
	 for(y=1; y<=10; y++) {
		 if(4*(int)x+5*(int)y ==60) {
			 System.out.println("x는 " + x + "이고 "+ "y는 "+y + "이다.");
			 
		 }
			 
	 }
 }
	}

}
