package exam01_If;

public class IfExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int score =85;//90;
		
		if(score >=90) {
			System.out.println(score + "점으로 등급은 A입니다.");
		} 
		
		if(score <90) {
			System.out.println(score + "점으로 등급은 B입니다.");
		}
		
		
	}
}
